import React from 'react';

type numDrops = number | undefined;
type dropletColor = `rgb(${string},${string},${string})` | undefined;
type size = 'short' | 'default' | 'long' | undefined;
type showImpact = boolean | undefined;
type rainEffect = 'rainbow' | undefined;
type dropletOpacity = number | undefined;
interface dropletOptions {
    numDrops?: numDrops;
    dropletColor?: dropletColor;
    size?: size;
    showImpact?: showImpact;
    rainEffect?: rainEffect;
    dropletOpacity?: dropletOpacity;
}

declare const Rain: React.FunctionComponent<dropletOptions>;

export { Rain };
